import { ChangeDetectionStrategy, ChangeDetectorRef, Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-encoder',
  templateUrl: './encoder.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class EncoderComponent {
  public streamedResults: string[] = [];
  myForm: FormGroup = this.formBuilder.group({
    textToEncode: ['', [Validators.required]]
  });

  constructor( private readonly cd: ChangeDetectorRef, private formBuilder: FormBuilder) {
    
  }

  public encodeText() {
    if (this.myForm.invalid) {
      this.myForm.markAllAsTouched();
      return;
    }
    const textToEncode = this.myForm.controls['textToEncode']?.value;
    this.getEncodedStreamed(textToEncode);
  }

  public async getEncodedStreamed(textToEncode: string): Promise<void> {
    const response = await fetch(`https://localhost:7141/Encoder/EncodeBase64V2?textToEncode=${textToEncode}`, {
      allowHTTP1ForStreamingUpload: true,
    } as any);
    if (response.body == null) {
      throw new Error('Not possible in current scenario');
    }
    const reader = response.body.getReader();

    const streamedResults = this.streamedResults;
    const cd = this.cd;

    async function printStream(): Promise<void> {
      const { done, value } = await reader.read();
      if (done) {
        return;
      }
      const textDecoder = new TextDecoder();
      const stringValue = textDecoder.decode(value);

      streamedResults.push(stringValue);
      cd.markForCheck();
     
      await printStream();
    }
    
    await printStream();
  }

  validField(field: string) {
    return this.myForm.controls[field]?.errors && this.myForm.controls[field]?.touched
  }

  


}

<h2>Encoder: <small>Base64</small></h2>
<hr>
<div class="row">
  <div class="col">
    <form (ngSubmit)="encodeText()" [formGroup]="myForm">

      <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Text To Encode:</label>
        <div class="col-sm-9">
          <input type="text"
                 class="form-control"
                 placeholder="Text to encode"
                 formControlName="textToEncode">

          <span class="form-text text-danger"
                *ngIf="validField('textToEncode')">
            Required Field
          </span>
        </div>
      </div>

      <div class="mb-3 row">

        <div class="col">
          <button type="submit"
                  class="btn btn-primary float-end">
            Convert
          </button>
        </div>
      </div>


    </form>
    <div class="mb-3 row">

      <label class="col-sm-3 col-form-label">Encoded Text:</label>
      <div class="col-sm-9">
        <span class="form-text form-control">
          {{streamedResults.join("")}}
        </span>
      </div>

    </div>
    <div class="col">
      <button type="button" (ngSubmit)="StopStream()"
              class="btn btn-danger float-end">
        Cancel
      </button>
    </div>
  </div>

</div>
